mapper.select:
    select 
	question_id,
	score
    from j_paper_question
    where paper_id = #{paperId,jdbcType=BIGINT}
    ORDER BY order_index ASC;

mapper.update:
    update manage_system.j_paper_question set 
    score = #{score,jdbcType=REAL}
    where question_id = #{questionId,jdbcType=BIGINT}
    and paper_id = #{paperId,jdbcType=BIGINT}
